// Importing React States
import React, { useState, useEffect } from "react";

// Importing Movie Card
import MovieCard from "./MovieCard";

// Importing Search Icon
import SearchIcon from "./search.svg";

// Importing CSS
import "./App.css";

// API-OMDB-Url
const API_URL = "http://www.omdbapi.com?apikey=b6003d8a";

// Initialzing the App

// We Convert the Function App to Const App
// function App(){}
// const App = () => {}

const App = () => {

    // async means asyncronous that means it will take some time to load  async arrow function
    const searchMovies = async(title) => {

        // Storing the Response received 
        const response = await fetch(`${API_URL}&s=${title}`);

        // Storing in data the response received in Json Format
        const data = await response.json();
    }

    // State Executed as soon as the Page is loaded
    // }, []); [] contains variables if changed then the state will be reloaded.
    useEffect(() => {
        searchMovies("Spiderman")
    }, []);

    return (
        <div className="app">

            <h1> MovieLand </h1>

            <div className="search">

                {/* Input Place Holder */}
                <input placeholder="Search for Movies" value="Superman" onChange ={() => {}}/>

                {/* // This {() => {}} is a nameless functions which executes the code in the brackets when clicked */}

                {/* Search Image */}
                <img src={SearchIcon} alt="search" onClick = {() => {}}/>

            </div>
        </div>
    );
}

export default App;
