import React, { useState, useEffect } from "react";

import MovieCard from "./MovieCard.jsx";
import SearchIcon from "./search.svg";
import "./App.css";

const API_URL = "http://www.omdbapi.com?apikey=b6003d8a";

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setsearchTerm] = useState('');

    // async means asyncronous that means it will take some time to load  async arrow function
    const searchMovies = async(title) => {

        // Storing the Response received 
        const response = await fetch(`${API_URL}&s=${title}`);

        // Storing in data the response received in Json Format
        const data = await response.json();

        setMovies(data.Search);
    }

    // State Executed as soon as the Page is loaded
    // }, []); [] contains variables if changed then the state will be reloaded.

    useEffect(() => {
        searchMovies("Spider-Man");
    }, []);

    return (
        <div className="app">
          <h1>MovieLand</h1>

          <div className="search">
            <input
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search for movies"
            />
            <img
              src={SearchIcon}
              alt="search"
              onClick={() => searchMovies(searchTerm)}
            />
          </div>

          {movies?.length > 0 ? (
            <div className="container">
              {movies.map((movie) => (
                <MovieCard movie={movie} />
              ))}
            </div>
          ) : (
            <div className="empty">
              <h2>No movies found</h2>
            </div>
          )}
        </div>
      );
    };
    ;

export default App;
